package com.shristi.basic;

import java.util.Scanner;

public class PatternRightAngleNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i, j, n = 4;

		System.out.println("Number Pattern ");

		for (i = 1; i <= n; i++) {

			for (j = 1; j <= i; j++) {

				System.out.print(i);

			}

			System.out.println("");
		}

	}

}
